// export default CreateResume;
import React, { useState } from 'react';

function CreateResume({ addResume, onViewCreatedResume }) {
  const [resume, setResume] = useState({
    fullName: '',
    phoneNumber: '',
    email: '',
    linkedin: '',
    portfolio: '',
    summary: '',
    skills: '',
    workExperience: '',
    education: '',
    certifications: '',
    projects: '',
    additional: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setResume((prevResume) => ({ ...prevResume, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newResume = { ...resume, id: Date.now() }; // Add a unique ID to the résumé
    addResume(newResume); // Pass the new résumé back to the parent
    onViewCreatedResume(newResume); // Immediately view the created résumé
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Create Résumé</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          type="text"
          name="fullName"
          placeholder="Full Name"
          value={resume.fullName}
          onChange={handleChange}
          style={styles.input}
          required
        />
        <input
          type="tel"
          name="phoneNumber"
          placeholder="Phone Number"
          value={resume.phoneNumber}
          onChange={handleChange}
          style={styles.input}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={resume.email}
          onChange={handleChange}
          style={styles.input}
          required
        />
        <input
          type="url"
          name="linkedin"
          placeholder="LinkedIn Profile"
          value={resume.linkedin}
          onChange={handleChange}
          style={styles.input}
        />
        <input
          type="url"
          name="portfolio"
          placeholder="Portfolio URL"
          value={resume.portfolio}
          onChange={handleChange}
          style={styles.input}
        />
        <textarea
          name="summary"
          placeholder="Professional Summary"
          value={resume.summary}
          onChange={handleChange}
          style={styles.textarea}
        />
        <textarea
          name="skills"
          placeholder="Skills (comma-separated)"
          value={resume.skills}
          onChange={handleChange}
          style={styles.textarea}
        />
        <textarea
          name="workExperience"
          placeholder="Work Experience"
          value={resume.workExperience}
          onChange={handleChange}
          style={styles.textarea}
        />
        <textarea
          name="education"
          placeholder="Education"
          value={resume.education}
          onChange={handleChange}
          style={styles.textarea}
        />
        <textarea
          name="certifications"
          placeholder="Certifications"
          value={resume.certifications}
          onChange={handleChange}
          style={styles.textarea}
        />
        <textarea
          name="projects"
          placeholder="Projects"
          value={resume.projects}
          onChange={handleChange}
          style={styles.textarea}
        />
        <textarea
          name="additional"
          placeholder="Additional Information"
          value={resume.additional}
          onChange={handleChange}
          style={styles.textarea}
        />
        <button type="submit" style={styles.submitButton}>
          Save and View Résumé
        </button>
      </form>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '600px',
    margin: '0 auto',
    padding: '20px',
    borderRadius: '8px',
    backgroundColor: '#f9f9f9',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    fontFamily: 'Arial, sans-serif',
  },
  heading: {
    textAlign: 'center',
    marginBottom: '20px',
    color: '#333',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
  },
  input: {
    marginBottom: '10px',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ccc',
  },
  textarea: {
    marginBottom: '10px',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    resize: 'vertical',
  },
  submitButton: {
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: 'none',
    cursor: 'pointer',
    backgroundColor: '#007BFF',
    color: '#fff',
    transition: 'background-color 0.3s',
  },
};

export default CreateResume;
