import React, { useState } from 'react';
import LandingPage from './LandingPage';
import CreateResume from './CreateResume';
import ResumeList from './ResumeList';
import ViewResume from './ViewResume';
import './App.css';

function App() {
  const [currentPage, setCurrentPage] = useState('landing'); // Tracks current page state
  const [resumes, setResumes] = useState(() => {
    // Load resumes from localStorage on initialization
    const savedResumes = localStorage.getItem('resumes');
    return savedResumes ? JSON.parse(savedResumes) : [];
  });
  const [selectedResume, setSelectedResume] = useState(null); // Tracks selected résumé for viewing

  // Handle navigation to Create Résumé page
  const handleCreateResume = () => setCurrentPage('create');

  // Handle navigation to View Résumé page with selected résumé
  const handleViewResume = (resume) => {
    setSelectedResume(resume);
    setCurrentPage('view');
  };

  // Handle navigation back to Landing Page
  const handleBackToLanding = () => {
    setCurrentPage('landing');
    setSelectedResume(null); // Reset selected résumé
  };

  // Add a new résumé to the list
  const addResume = (newResume) => {
    const updatedResumes = [...resumes, newResume];
    setResumes(updatedResumes);
    localStorage.setItem('resumes', JSON.stringify(updatedResumes)); // Persist to localStorage
  };

  return (
    <div>
      {/* Landing Page */}
      {currentPage === 'landing' && (
        <LandingPage
          onCreateResume={handleCreateResume}
          onViewResume={() => setCurrentPage('list')} // Navigate to Résumé List
        />
      )}

      {/* Create Résumé Page */}
      {currentPage === 'create' && (
        <CreateResume
          addResume={(data) => {
            addResume(data);
            setCurrentPage('list'); // Navigate to Résumé List after creation
          }}
        />
      )}

      {/* Résumé List Page */}
      {currentPage === 'list' && (
        <ResumeList
          resumes={resumes}
          onViewResume={handleViewResume} // Pass the handleViewResume function
        />
      )}

      {/* View Résumé Page */}
      {currentPage === 'view' && selectedResume && (
        <ViewResume resume={selectedResume} />
      )}

      {/* Back Button */}
      {currentPage !== 'landing' && (
        <button onClick={handleBackToLanding} className="back-button">
          Back
        </button>
      )}
    </div>
  );
}

export default App;
