import React, { useState } from 'react';
import LandingPage from './LandingPage';
import CreateResume from './CreateResume';
import ResumeList from './ResumeList';
import ViewResume from './ViewResume';
import './App.css';

function App() {
  const [currentPage, setCurrentPage] = useState('landing');
  const [resumes, setResumes] = useState(() => {
    // Load resumes from localStorage on initialization
    const savedResumes = localStorage.getItem('resumes');
    return savedResumes ? JSON.parse(savedResumes) : [];
  });
  const [selectedResume, setSelectedResume] = useState(null);

  const handleCreateResume = () => setCurrentPage('create');
  const handleViewResume = (resume) => {
    setSelectedResume(resume);
    setCurrentPage('view');
  };
  const handleBackToLanding = () => setCurrentPage('landing');

  const addResume = (newResume) => {
    const updatedResumes = [...resumes, newResume];
    setResumes(updatedResumes);
    localStorage.setItem('resumes', JSON.stringify(updatedResumes)); // Save to localStorage
  };

  return (
    <div>
      {/* Landing Page */}
      {currentPage === 'landing' && (
        <LandingPage onCreateResume={handleCreateResume} onViewResume={() => setCurrentPage('list')} />
      )}

      {/* Create Résumé Page */}
      {currentPage === 'create' && (
        <CreateResume
          addResume={(data) => {
            addResume(data);
            setCurrentPage('list'); // Navigate to the résumé list after creation
          }}
        />
      )}

      {/* Résumé List Page */}
      {currentPage === 'list' && (
        <ResumeList
          resumes={resumes}
          onViewResume={handleViewResume}
        />
      )}

      {/* View Résumé Page */}
      {currentPage === 'view' && selectedResume && (
        <ViewResume resume={selectedResume} />
      )}

      {/* Back to Landing Page */}
      {currentPage !== 'landing' && (
        <button onClick={handleBackToLanding} className="back-button">
          Back
        </button>
      )}
    </div>
  );
}

export default App;
