import React, { useState } from 'react';
import LandingPage from './LandingPage';
import CreateResume from './CreateResume';
import ResumeList from './ResumeList';
import ResumePreview from './ResumePreview';
import './App.css';

function App() {
    const [currentPage, setCurrentPage] = useState('landing');

    const handleCreateResume = () =>  setCurrentPage('create');
    const handleViewResume = () => setCurrentPage('view');
    const handleEditResume = () => setCurrentPage('edit');
    const handleBackToLanding = () => setCurrentPage('landing');
    const [resumes, setResumes] = useState([]);


    const updateResume = (updatedResume) => {
        const updatedResumes = resumes.map((resume) =>
          resume.id === updatedResume.id ? updatedResume : resume
        );
        setResumes(updatedResumes);
        localStorage.setItem('resumes', JSON.stringify(updatedResumes));
      };

    return (
        <div>
            {/* Display the Landing Page  as the default view */}
            {currentPage === 'landing' &&  (
                <LandingPage
                    onCreateResume={handleCreateResume}
                    onViewResume={handleViewResume}
                    onEditResume={handleEditResume}
                />
            )}
  {/* Navigate to CreateResume */}
   {currentPage === 'create' && <CreateResume addResume={(data) => console.log(data)} />}
    {/* Navigate to ResumeList */}
    {currentPage === 'list' && <ResumeList />}
    {/* Navigate to ResumePreview */}
    {currentPage === 'preview' && <ResumePreview />}
    {/* Back to Landing Page */}
            {currentPage !== 'landing' && (
                <button onClick={handleBackToLanding} className="back-button">
                    Back
                </button>
            )}
        </div>
    )
}
export default App;
