import React from 'react';
import jsPDF from 'jspdf';
import './ViewResume.css';

function ViewResume({ resume }) {
  const downloadResume = () => {
    const doc = new jsPDF();
    doc.setFontSize(22);
    doc.text('Resume', 105, 10, { align: 'center' });

    doc.setFontSize(12);
    doc.text(`Full Name: ${resume.fullName}`, 10, 30);
    doc.text(`Phone Number: ${resume.phoneNumber}`, 10, 40);
    doc.text(`Email: ${resume.email}`, 10, 50);
    doc.text(`LinkedIn: ${resume.linkedin}`, 10, 60);
    doc.text(`Portfolio: ${resume.portfolio}`, 10, 70);
    doc.text(`Summary: ${resume.summary}`, 10, 80);
    doc.text(`Skills: ${resume.skills}`, 10, 90);
    doc.text(`Work Experience: ${resume.workExperience}`, 10, 100);
    doc.text(`Education: ${resume.education}`, 10, 110);
    doc.text(`Certifications: ${resume.certifications}`, 10, 120);
    doc.text(`Projects: ${resume.projects}`, 10, 130);
    doc.text(`Additional Information: ${resume.additional}`, 10, 140);

    doc.save(`${resume.fullName}_Resume.pdf`);
  };

  return (
    <div className="view-resume-container">
      <h2 className="view-resume-heading">{resume.fullName}'s Résumé</h2>
      <div className="resume-details">
        <p><strong>Phone:</strong> {resume.phoneNumber}</p>
        <p><strong>Email:</strong> {resume.email}</p>
        <p><strong>LinkedIn:</strong> {resume.linkedin}</p>
        <p><strong>Portfolio:</strong> {resume.portfolio}</p>
        <p><strong>Summary:</strong> {resume.summary}</p>
        <p><strong>Skills:</strong> {resume.skills}</p>
        <p><strong>Work Experience:</strong> {resume.workExperience}</p>
        <p><strong>Education:</strong> {resume.education}</p>
        <p><strong>Certifications:</strong> {resume.certifications}</p>
        <p><strong>Projects:</strong> {resume.projects}</p>
        <p><strong>Additional Info:</strong> {resume.additional}</p>
      </div>
      <button onClick={downloadResume} className="download-button">Download PDF</button>
    </div>
  );
}

export default ViewResume;
