import React from 'react';
import jsPDF from 'jspdf';
import './ViewResume.css';

function ViewResume({ resume }) {
  // Function to generate and download the resume as a PDF
  const downloadResume = async () => {
    const doc = new jsPDF();

    doc.setFontSize(22);
    doc.text('Resume', 105, 10, { align: 'center' });

    // Add the photo if it exists
    if (resume.photo) {
      const img = new Image();
      img.src = resume.photo;

      // Wait for the image to load before adding to the PDF
      await new Promise((resolve) => {
        img.onload = () => {
          doc.addImage(img, 'JPEG', 80, 20, 50, 50); // Adjust x, y, width, height as needed
          resolve();
        };
      });
    }

    doc.setFontSize(12);
    doc.text(`Full Name: ${resume.fullName}`, 10, 80);
    doc.text(`Phone Number: ${resume.phoneNumber}`, 10, 90);
    doc.text(`Email: ${resume.email}`, 10, 100);
    doc.text(`LinkedIn: ${resume.linkedin}`, 10, 110);
    doc.text(`Portfolio: ${resume.portfolio}`, 10, 120);
    doc.text(`Summary: ${resume.summary}`, 10, 130);
    doc.text(`Skills: ${resume.skills}`, 10, 140);
    doc.text(`Work Experience: ${resume.workExperience}`, 10, 150);
    doc.text(`Education: ${resume.education}`, 10, 160);
    doc.text(`Certifications: ${resume.certifications}`, 10, 170);
    doc.text(`Projects: ${resume.projects}`, 10, 180);
    doc.text(`Additional Information: ${resume.additional}`, 10, 190);

    doc.save(`${resume.fullName}_Resume.pdf`);
  };

  return (
    <div className="view-resume-container">
      <h2 className="view-resume-heading">{resume.fullName}'s Résumé</h2>
      {resume.photo && (
        <div className="resume-photo">
          <img src={resume.photo} alt="User Photo" className="resume-photo-image" />
        </div>
      )}
      <div className="resume-details">
        <p><strong>Phone:</strong> {resume.phoneNumber}</p>
        <p><strong>Email:</strong> {resume.email}</p>
        <p><strong>LinkedIn:</strong> {resume.linkedin}</p>
        <p><strong>Portfolio:</strong> {resume.portfolio}</p>
        <p><strong>Summary:</strong> {resume.summary}</p>
        <p><strong>Skills:</strong> {resume.skills}</p>
        <p><strong>Work Experience:</strong> {resume.workExperience}</p>
        <p><strong>Education:</strong> {resume.education}</p>
        <p><strong>Certifications:</strong> {resume.certifications}</p>
        <p><strong>Projects:</strong> {resume.projects}</p>
        <p><strong>Additional Info:</strong> {resume.additional}</p>
      </div>
      {/* Download PDF Button */}
      <button onClick={downloadResume} className="download-button">Download PDF</button>
    </div>
  );
}

export default ViewResume;
