import React, { useState } from 'react';
import LandingPage from './LandingPage';
import CreateResume from './CreateResume';
import ResumeList from './ResumeList';

import './App.css';

function App() {
  const [currentPage, setCurrentPage] = useState('landing');
  const [resumes, setResumes] = useState(() => {
    // Load resumes from localStorage on initialization
    const savedResumes = localStorage.getItem('resumes');
    return savedResumes ? JSON.parse(savedResumes) : [];
  });
  const [selectedResume, setSelectedResume] = useState(null);

  const handleCreateResume = () => setCurrentPage('create');
  const handleViewResume = () => setCurrentPage('list');
  const handleEditResume = (resume) => {
    setSelectedResume(resume);
    setCurrentPage('edit');
  };
  const handlePreviewResume = (resume) => {
    setSelectedResume(resume);
    setCurrentPage('preview');
  };
  const handleBackToLanding = () => setCurrentPage('landing');

  // Add a new resume
  const addResume = (newResume) => {
    const updatedResumes = [...resumes, newResume];
    setResumes(updatedResumes);
    localStorage.setItem('resumes', JSON.stringify(updatedResumes));
  };

  // Update an existing resume
  const updateResume = (updatedResume) => {
    const updatedResumes = resumes.map((resume) =>
      resume.id === updatedResume.id ? updatedResume : resume
    );
    setResumes(updatedResumes);
    localStorage.setItem('resumes', JSON.stringify(updatedResumes));
  };

  return (
    <div>
      {/* Display the Landing Page as the default view */}
      {currentPage === 'landing' && (
        <LandingPage
          onCreateResume={handleCreateResume}
          onViewResume={handleViewResume}
          onEditResume={handleEditResume}
        />
      )}

      {/* Navigate to CreateResume */}
      {currentPage === 'create' && (
        <CreateResume
          addResume={(data) => {
            addResume(data);
            setCurrentPage('list'); // Navigate to the résumé list after creation
          }}
        />
      )}

      {/* Navigate to ResumeList */}
      {currentPage === 'list' && (
        <ResumeList
          resumes={resumes}
          onPreviewResume={handlePreviewResume}
          onEditResume={handleEditResume}
        />
      )}

      {/* Navigate to ResumePreview */}
      {currentPage === 'preview' && selectedResume && (
        <ResumePreview resume={selectedResume} />
      )}

      {/* Navigate to EditResume */}
      {currentPage === 'edit' && selectedResume && (
        <CreateResume
          addResume={(data) => {
            updateResume(data);
            setCurrentPage('list'); // Navigate back to the résumé list after editing
          }}
          initialResumeData={selectedResume}
        />
      )}

      {/* Back to Landing Page */}
      {currentPage !== 'landing' && (
        <button onClick={handleBackToLanding} className="back-button">
          Back
        </button>
      )}
    </div>
  );
}

export default App;
