import React, { useState } from 'react';
import './CreateResume.css';

function CreateResume({ addResume }) {
  const [formData, setFormData] = useState({
    fullName: '',
    phoneNumber: '',
    email: '',
    linkedin: '',
    portfolio: '',
    summary: '',
    skills: '',
    workExperience: '',
    education: '',
    certifications: '',
    projects: '',
    additional: '',
    photo: null, // Add a field for the photo
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setFormData({ ...formData, photo: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addResume({ ...formData, id: Date.now() });
    alert('Résumé created successfully!');
    setFormData({
      fullName: '',
      phoneNumber: '',
      email: '',
      linkedin: '',
      portfolio: '',
      summary: '',
      skills: '',
      workExperience: '',
      education: '',
      certifications: '',
      projects: '',
      additional: '',
      photo: null,
    });
  };

  return (
    <div className="create-resume">
      <h2>Create Résumé</h2>

      {/* Photo Upload Section */}
      <div className="photo-upload">
        <label>
          {formData.photo ? (
            <img
              src={formData.photo}
              alt="Uploaded"
              className="photo-preview"
            />
          ) : (
            <div className="photo-placeholder">Photo Placeholder</div>
          )}
          <input
            type="file"
            accept="image/*"
            onChange={handlePhotoChange}
            style={{ display: 'none' }}
          />
        </label>
      </div>

      <form onSubmit={handleSubmit}>
        <label>
          Full Name:
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Phone Number:
          <input
            type="tel"
            name="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          LinkedIn Profile:
          <input
            type="url"
            name="linkedin"
            value={formData.linkedin}
            onChange={handleChange}
          />
        </label>
        <label>
          Portfolio/Website:
          <input
            type="url"
            name="portfolio"
            value={formData.portfolio}
            onChange={handleChange}
          />
        </label>
        <label>
          Professional Summary or Objective:
          <textarea
            name="summary"
            value={formData.summary}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Skills:
          <textarea
            name="skills"
            placeholder="List your skills (e.g., Technical Skills, Soft Skills)"
            value={formData.skills}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Work Experience:
          <textarea
            name="workExperience"
            placeholder="Include job title, company name, dates, and achievements"
            value={formData.workExperience}
            onChange={handleChange}
          />
        </label>
        <label>
          Education:
          <textarea
            name="education"
            placeholder="Degree, Institution Name, Location, Graduation Date"
            value={formData.education}
            onChange={handleChange}
          />
        </label>
        <label>
          Certifications or Licenses:
          <textarea
            name="certifications"
            placeholder="Certification Name, Issuing Organization, Date Earned"
            value={formData.certifications}
            onChange={handleChange}
          />
        </label>
        <label>
          Projects or Portfolio Highlights:
          <textarea
            name="projects"
            placeholder="Project Title, Description, Tools Used, Links"
            value={formData.projects}
            onChange={handleChange}
          />
        </label>
        <label>
          Additional Sections (Languages, Volunteer Work, etc.):
          <textarea
            name="additional"
            placeholder="Include any additional information you want to highlight"
            value={formData.additional}
            onChange={handleChange}
          />
        </label>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default CreateResume;
