// export default App;




function App() {
  const [resumes, setResumes] = useState([]);

  // Function to add a new résumé to the list
  const addResume = (newResume) => {
    setResumes([...resumes, newResume]);
    console.log('Résumé added:', newResume); // Log the new résumé for debugging
  };

  return (
    <div>
      <h1>Résumé Builder</h1>
      <CreateResume addResume={addResume} />
      <h2>Saved Résumés</h2>
      <ul>
        {resumes.map((resume) => (
          <li key={resume.id}>
            <strong>{resume.fullName}</strong> - {resume.email}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
